import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import Weather from "./components/Weather";

const API_KEY = "1fd777d6987a67e150b5b11cb99e7ec1";

class App extends React.Component{
  getWeather = async() => {
    const api_call = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=Nice&mode=json&appid=${API_KEY}&units=metric`);
    const data = await api_call.json();
    console.log(data);
  }
  render(){
    return(
      <div>
        <Titles/>
        <Form/>
        <Weather/>
      </div>

    );
  }
}

export default App;
